mapper.update:
UPDATE Products
SET is_visible = FALSE
WHERE id = #{productId} AND current_participants >= max_participants;

UPDATE productrequests
SET approval_status = #{approvalStatus}, status = '완료', updated_at = NOW()
WHERE id = #{requestId};

UPDATE Products
SET is_visible = FALSE
WHERE id = #{productId}
  AND (
          SELECT COUNT(*) FROM productrequests
          WHERE product_id = #{productId}
            AND approval_status = '승인'
      ) >= max_participants;

UPDATE productrequests
SET status = '완료', updated_at = NOW()
WHERE product_id = #{productId}
  AND approval_status = '승인';

mapper.select:
SELECT requester_email
FROM productrequests
WHERE id = #{requestId}

SELECT
p.*, u.nickname, u.bio, p.days,
COALESCE(pd.dopamine, 50) AS dopamine, pi.image_path
FROM products p
LEFT JOIN users u ON p.email = u.email
LEFT JOIN productimages pi ON p.id = pi.product_id
LEFT JOIN (
SELECT email, SUM(dopamine) AS dopamine
FROM pointsdopamineactivity
GROUP BY email
) pd ON u.email = pd.email
WHERE p.id = #{id}
AND (
p.is_visible = TRUE
OR p.email = #{email}  
OR EXISTS (
SELECT 1 FROM productrequests pr
WHERE pr.product_id = p.id
AND pr.requester_email = #{email}
AND pr.approval_status = '승인'  
)
);

SELECT p.*, u.nickname, u.bio, p.days, COALESCE(pd.dopamine, 50) AS dopamine, pi.image_path FROM products p LEFT JOIN users u ON p.email = u.email LEFT JOIN productimages pi ON p.id = pi.product_id LEFT JOIN ( SELECT email, SUM(dopamine) AS dopamine FROM pointsdopamineactivity GROUP BY email ) pd ON u.email = pd.email WHERE p.email = #{email} AND p.is_visible = TRUE AND p.registration_type IN
#{type}
ORDER BY p.created_at DESC;

SELECT p.*, u.nickname, u.bio, p.days, COALESCE(pd.dopamine, 50) AS dopamine, pi.image_path FROM products p LEFT JOIN users u ON p.email = u.email LEFT JOIN productimages pi ON p.id = pi.product_id LEFT JOIN ( SELECT email, SUM(dopamine) AS dopamine FROM pointsdopamineactivity GROUP BY email ) pd ON u.email = pd.email WHERE p.is_visible = TRUE
OR p.email = #{email}
ORDER BY p.id DESC;

SELECT approval_status
FROM productrequests
WHERE requester_email = #{email}
  AND product_id = #{productId}
ORDER BY created_at DESC
LIMIT 1;
