mapper.update:
UPDATE Users
SET login_failed_attempts = #{attempts}
WHERE email = #{email}

UPDATE Users
SET login_is_locked = #{isLocked}
WHERE email = #{email}

UPDATE Users
SET last_login_time = NOW()
WHERE email = #{email}

UPDATE Users
SET password_hash = #{newPasswordHash}
WHERE email = #{email}

UPDATE User_Account_info
SET account_status = #{accountStatus}
WHERE email = #{email}
